sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === ''||newValue==oldValue) {
      return;
   }

   //Type appropriate comment here, and begin script below
	var getEmailAddress=new GlideAjax('GetEmailAddress');
	getEmailAddress.addParam('sysparm_name','getEmail');
	getEmailAddress.addParam('sysparm_userID',newValue);

	getEmailAddress.getXML(populateEmailField);

	function populateEmailField(response){

		var emailFromScriptInlcude;
		try{
 emailFromScriptInlcude=response.responseXML.documentElement.getAttribute("answer");
		}
		catch(err)
		{
		jslog("exerciase ERROR: "+err);
		return;
		}
	    jslog("before g_form.clearValue");		
		g_form.clearValue("u_requested_for_email");
		g_form.setValue("u_requested_for_email",emailFromScriptInlcude);
	}

}
